import subprocess,os,configparser
class RNS:
    """RNS Controle Class"""
    def __init__(self):
        self.setting  = configparser.ConfigParser()
        self.setting.read("../config.ini")
        self.version = self.setting["version"]["version"]
        self.env = configparser.ConfigParser()
        self.env.read("../temp.ini")
        self.env = self.env["env"]
class Config:
    """RNS Config Controller"""

    def __init__(self, name):
        self.name = name
        self.config = configparser.ConfigParser()
        self.filepath = f"../Config/{name}.ini"
        if os.path.exists(self.filepath):
            self.config.read(self.filepath)
        else:
            with open(self.filepath, "w") as f:
                pass
            self.config.read(self.filepath)
    def save(self):
        """save config"""
        with open(self.filepath, "w") as f:
            self.config.write(f)